import React from 'react'
import './App.css';
import SearchBar from './Components/SearchBar/SearchBar'
import SearchResults from './Components/SearchResults/SearchResults'
import Playlist from './Components/Playlist/Playlist'


class App extends React.Component {
  constructor(props){
    super(props)
    this.state={
      searchResults: [
        {name:'name 1', artist: "atrist1",album:"album1",id:1},
        {name:'name 2', artist: "atrist2",album:"album2",id:2},
        {name:'name 3', artist: "atrist3",album:"album3",id:3},
        {name:'name 4', artist: "atrist4",album:"album4",id:4},
      ],
      playlistName: 'baobao-playlist',
      playlistTracks: [
        {name:'playlist 1', artist: "playlist-atrist1",album:"playlist-album1",id:1,uri:"123"},
        {name:'playlist 2', artist: "playlist-atrist2",album:"playlist-album2",id:2,uri:"123"},
        {name:'playlist 3', artist: "playlist-atrist3",album:"playlist-album3",id:3,uri:"123"},
        {name:'playlist 4', artist: "playlist-atrist4",album:"playlist-album4",id:4,uri:"123"},
      ]
    };
    this.addTrack = this.addTrack.bind(this);
    this.removeTrack = this.removeTrack.bind(this);
    this.updatePlaylistName = this.updatePlaylistName.bind(this);
    this.savePlaylist = this.savePlaylist.bind(this);
    this.search = this.search.bind(this);



  }

  addTrack(track) {
    let tracks = this.state.playlistTracks;
   if (tracks.find(savedTrack => savedTrack.id === track.id)) {
     return;
   }
   tracks.push(track);
   this.setState({playlistTracks: tracks})
 }

 removeTrack(track){ 
 let tracks = this.state.playlistTracks;
 tracks = tracks.filter(currentTracks => currentTracks.id !== track.id)
 this.setState({
   playlistTracks:tracks
 })   
 }

 updatePlaylistName(name){
     this.setState({
      playlistName:name
    })  
 }

 savePlaylist(){
   const trackURIs = this.state.playlistTracks.map(track=>track.uri)
 }

 search(term){
    console.log(term)
 }


  render(){


    return (
      <div>
      <h1>Ja<span className="highlight">mmm</span>ing</h1>
      <div className="App">
       <SearchBar 
           onSearch={this.search}/>
        <div className="App-playlist">
          <SearchResults searchResults={this.state.searchResults} onAdd={this.addTrack}/>
          <Playlist 
              playlistName={this.state.playlistName} 
              playlistTracks={this.state.playlistTracks}
              onRemove= {this.removeTrack}
              onNameChange = {this.updatePlaylistName}
              onSave={this.savePlaylist}
              /> 
        </div>
      </div>
    </div>
  );

  }
  }

  export default App;
